import React from 'react';
import axios from 'axios';
import logo from '../assets/img/logo.svg';
import '../assets/css/App.css';

import Sidebar from '../components/Sidebar';

export default class App extends React.Component {


  constructor(){
    super();
    this.state = {
      resultData: [],
      loader: true,
      sidebar: false,
      playerName: '',
      tourneyYear: 0,
      tourneyLocation: '',
      tourneyDate: ''
    }
  }

  componentDidMount() {
    this.listResuls();
  }

  listResuls = () => {
        axios.get('http://localhost/atpgrandslam/src/api/')
            .then(res => {
              this.setState({resultData: res.data.data, loader:false});          
        });
  }

  openSidebarInfo = (data) => {
    console.log(data);
      this.setState({
        sidebar: true, 
        playerName: data.player_name,
        tourneyYear: data.innerLast,
        tourneyLocation: data.tourney_location,
        tourneyDate: data.tourney_dates
      });
  }
  closeSidebarInfo = () => {
    this.setState({sidebar: false});
  }

  render(){

    const {resultData, loader, sidebar} = this.state;

    return (
      <React.Fragment>
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
        </header>
        <div className="tableResul">
            <header className="header">
              <strong>Grand Slam (tenis)</strong>
              <p>El Grand Slam de tenis está constituido por los cuatro torneos mayores del circuito internacional organizados por la Federación Internacional de Tenis,</p>
            </header>

            <div className="headerTable">
              <div>Torneo</div>
              <div>Jugador</div>
            </div>
            <div className="listResult">
             {
              resultData.length > 0 &&
              resultData.map((item, key) => {
                  return(
                    <div className="itemResult">
                      <div>{item.tourney_name}</div>
                      <div><a onClick={() => this.openSidebarInfo(item)}>{item.player_name}</a></div>
                    </div>
                  )
                })
             }
             {
               loader && <div class="conteneur_general_load_9" align="center"> <div class="loader_9"></div> </div>
             }
            </div>

            <Sidebar 
              sidebar={sidebar} 
              playerName = {this.state.playerName}
              tourneyYear ={this.state.tourneyYear}
              tourneyLocation = {this.state.tourney_location}
              tourneyDate = {this.state.tourney_dates}
              closeSidebarInfo = {this.closeSidebarInfo}
            />

        </div>
      </div>
      </React.Fragment>
    );
  }

}
