import React from 'react';
import axios from 'axios';
import logo from '../assets/img/logo.svg';
import copa from '../assets/img/copa.png';
import close from '../assets/img/close.png';
import '../assets/css/App.css';

export default class App extends React.Component {


  constructor(){
    super();
    this.state = {
      resultData: [],
      loader: true,
      sidebar: false
    }
  }

  componentDidMount() {
    this.listResuls();
  }

  listResuls = () => {
        axios.get('http://localhost/atpgrandslam/src/api/')
            .then(res => {
              this.setState({resultData: res.data.data, loader:false});          
        });
  }

  openSidebarInfo = () => {
      this.setState({sidebar: true});
  }
  closeSidebarInfo = () => {
    this.setState({sidebar: false});
  }

  render(){

    const {resultData, loader, sidebar} = this.state;

    return (
      <React.Fragment>
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
        </header>
        <div className="tableResul">
            <header className="header">
              <strong>Grand Slam (tenis)</strong>
              <p>El Grand Slam de tenis está constituido por los cuatro torneos mayores del circuito internacional organizados por la Federación Internacional de Tenis,</p>
            </header>

            <div className="headerTable">
              <div>Torneo</div>
              <div>Jugador</div>
            </div>
            <div className="listResult">
             {
              resultData.length > 0 &&
              resultData.map((item, key) => {
                  return(
                    <div className="itemResult">
                      <div>{item.tourney_name}</div>
                      <div><a onClick={() => this.openSidebarInfo()}>{item.player_name}</a></div>
                    </div>
                  )
                })
             }
             {
               loader && <div class="conteneur_general_load_9" align="center"> <div class="loader_9"></div> </div>
             }
            </div>

             <div className={`sidebarInfo ${sidebar ? 'show':''}`}>
                <header className="header">
                  <a onClick={()=>this.closeSidebar()}><img src={close} className="close" alt="Copa" /></a>
                  <span>Ultima vez que lo ganó</span>
                </header>
                <img src={copa} className="copa" alt="Copa" />
               <div className="infoPlayer">
                  <p className="titleName">Rafael Nadal</p>
                  <span>Año: 2018</span>
                  <span>Lugar: Melbourne, Australia</span>
                  <span>Fecha: 2017.05.28</span>
               </div>
             </div>

        </div>
      </div>
      </React.Fragment>
    );
  }

}
